import { interpolate as d3Interpolate } from 'd3-interpolate'
import * as flubber from 'flubber'

export function eitherIsAPoint (fromGeometry, toGeometry) {
  return fromGeometry.type === 'Point' || toGeometry.type === 'Point'
}

export function createPointTransition (fromGeometry, toGeometry) {
  if (fromGeometry.type === 'Point' && toGeometry.type === 'Point') {
    return d3Interpolate(fromGeometry, toGeometry)
  }

  if (fromGeometry.type === 'Point') {
    return flubber.fromCircle()
  }

  if (toGeometry.type === 'Point') {
    return flubber.toCircle()
  }
}
